import{_ as a,c as l,a0 as r,o as i}from"./chunks/framework.CbAMbEWr.js";const p=JSON.parse('{"title":"CourseHelper","description":"","frontmatter":{},"headers":[],"relativePath":"tools/CourseHelper.md","filePath":"tools/CourseHelper.md","lastUpdated":1731205388000}'),o={name:"tools/CourseHelper.md"};function t(s,e,h,n,u,c){return i(),l("div",null,e[0]||(e[0]=[r('<h1 id="coursehelper" tabindex="-1">CourseHelper <a class="header-anchor" href="#coursehelper" aria-label="Permalink to &quot;CourseHelper&quot;">​</a></h1><h2 id="项目仓库" tabindex="-1">项目仓库 <a class="header-anchor" href="#项目仓库" aria-label="Permalink to &quot;项目仓库&quot;">​</a></h2><p>Repo：<a href="https://github.com/WHUnicorn/CourseHelper" target="_blank" rel="noreferrer">CourseHelper</a></p><h2 id="这个项目是干什么的" tabindex="-1">这个项目是干什么的 <a class="header-anchor" href="#这个项目是干什么的" aria-label="Permalink to &quot;这个项目是干什么的&quot;">​</a></h2><h3 id="核心是选课" tabindex="-1">核心是选课： <a class="header-anchor" href="#核心是选课" aria-label="Permalink to &quot;核心是选课：&quot;">​</a></h3><ul><li>公共课由学校导入，但专业选修课等学分占比较大的课程要自己来选</li><li>教务系统并没有提供一个能比较全面地分析选课情况的工具</li><li>我都选了什么课？该选什么课？还差什么课需要选？</li><li>此项目旨在解决选课相关诸类的问题，且正在一步步开发完善</li></ul><h3 id="现在能干什么" tabindex="-1">现在能干什么： <a class="header-anchor" href="#现在能干什么" aria-label="Permalink to &quot;现在能干什么：&quot;">​</a></h3><ul><li>现在可以显示个人「专业课」都修了哪些（从教务系统实时导入）</li><li>当前已修课程和培养方案相比，进度如何（比如这个方向培养方案要求多少学分，我目前修了多少学分）</li><li>本地网页展示，后面我会尽力写个好看的界面（虽然现在界面约等于没有，大佬轻喷）</li><li>目前只有「计算机科学与技术」专业的培养方案对比，其他专业如有需求可联系作者或提 issue</li><li>其他任何和选课相关的功能，只要你想要，我马上写，Just a matter of time</li></ul><h2 id="这个项目安全吗" tabindex="-1">这个项目安全吗 <a class="header-anchor" href="#这个项目安全吗" aria-label="Permalink to &quot;这个项目安全吗&quot;">​</a></h2><ul><li>本项目所有代码开源</li><li>本项目仅在本地使用到了 cookie</li><li>从教务系统导入课程使用到的 cookie，如果您不主动分享给他人，是不会泄露的</li><li>经测试，教务系统获取的 cookie 只有不到一天有效期，每天都需要重新打开网页获取</li></ul><h2 id="我可以完全依赖该项目吗" tabindex="-1">我可以完全依赖该项目吗 <a class="header-anchor" href="#我可以完全依赖该项目吗" aria-label="Permalink to &quot;我可以完全依赖该项目吗&quot;">​</a></h2><ul><li>当然不行！！！</li><li>该项目还在开发中，目前发现教务系统部分公共课（比如马原）课头号发生变化，与培养方案不对应， 可能会出现项目中<code>已选</code>课程比自己实际选的课偏少的情况</li><li>专业课目前没发现上述情况，但不排除</li><li>使用过程中有任何问题请联系作者或 在项目里提 issue（最好后者，作者比较健忘……）</li></ul><h2 id="这个项目是怎么来的" tabindex="-1">这个项目是怎么来的 <a class="header-anchor" href="#这个项目是怎么来的" aria-label="Permalink to &quot;这个项目是怎么来的&quot;">​</a></h2><ul><li>作者为转专业人士，自己本来的课程就选得比较乱，且要补所有的计院专业课</li><li>出于对自己无法正常毕业的恐惧，以及解决选课头皮发麻问题而创建该项目（大佬轻喷）</li><li>开源出来是因为 自己不知道项目有没有潜在问题（希望大家帮忙反馈下） 业余时间写的、golang 新手，代码有点烂，希望得到大佬指点</li><li>觉得不错的话，求个小小的 star ……</li></ul><h2 id="关于作者" tabindex="-1">关于作者 <a class="header-anchor" href="#关于作者" aria-label="Permalink to &quot;关于作者&quot;">​</a></h2><ul><li>WHUCAO 开源社区某潜水群友，计科摆子（字面意思，目标是不挂科+正常毕业，求佬带带学习！！）</li><li>联系方式：1921567337@qq.com</li><li>作者博客：<a href="https://www.fordece.cn/" target="_blank" rel="noreferrer">https://www.fordece.cn/</a></li></ul><h2 id="后记" tabindex="-1">后记 <a class="header-anchor" href="#后记" aria-label="Permalink to &quot;后记&quot;">​</a></h2><ul><li>特别感谢：廖佬</li></ul>',18)]))}const f=a(o,[["render",t]]);export{p as __pageData,f as default};
